                write(clnt_sock, (const void *)fruits, sizeof(fruits));
                break;
            }
        default:
            break;
    }

    /** 另一次连结
    int clnt_sock2 = accept(serv_sock, (struct sockaddr *)&clnt_addr, &clnt_addr_size);
    if (clnt_sock2 == -1) {
