func swap(_ a: inout Int, _ b: inout Int) -> Void {
    let temp = a
    a = b
    b = temp
}

var a = 1
var b = 2
swap(&a, &b)
print("a: \(a) -- b: \(b)") // a: 2 -- b: 1

